sources.assets/styles/index.scss:
* {
  box-sizing: border-box;
}

html {
  font-family: Helvetica, Arial;
}

body {
  margin: 0;
  color: #6e6d7a;
  height: 100%;
  vertical-align: baseline;
  line-height: 24px;
}

ul,
p,
h1,
h2 {
  margin: 0;
  padding: 0;
  list-style: none;
}

a {
  text-decoration: none;
  color: #6e6d7a;

  &:active {
    color: black;
  }
}

.container {
  margin: auto;
  padding-left: 30px;
  padding-right: 30px;
  padding-top: 30px;
  width: 100%;
  background: #fff;
  z-index: 9996;
  color: #6e6d7a;
  position: fixed;
  will-change: transform;
  top: 0;
  height: 80px;
}

.header {
  box-shadow: inset 0px -1px 0px #f3f4f4;
}

.header-inner {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #fff;
  color: #6e6d7a;
  height: 80px;
}

.nav {
  height: inherit;
  overflow: visible;

  .links-container {
    display: none;
  }
}

.chevron {
  float: right;
  width: 6px;
  height: 6px;
  border-right: 1px solid black;
  border-bottom: 1px solid black;
  transform: rotate(45deg) translateX(6px);
}

a.burger {
  width: 60px;
  text-align: center;
  margin-left: -30px;
  line-height: 60px;
  svg {
    width: 20px;
    height: 20px;
  }
}

.nav-icon {
  height: 18px;
  width: 18px;
  color: #9e9ea7;
  position: relative;
  top: 4px;
}

.fill-current {
  fill: currentColor;
}

a.logo {
  padding-right: 20px;
  &:hover {
    opacity: 0.7;
  }
}

//auth-group
.auth-group {
  .auth-group-item:not(:nth-child(2)) {
    display: none;
  }
}

.nav-search {
  border: none;
  background-image: url(/assets/images/search.svg);
  background-repeat: no-repeat;
  background-position: 100% 50%;
  background-size: contain;
  opacity: 0.7;
}

.btn {
  padding: 10px 16px;
  cursor: pointer;
  border: none;
  text-decoration: none;
  background: #ea4c89;
  -webkit-transition: all 200ms ease;
  transition: all 200ms ease;
  border-radius: 8px;
  height: 40px;
  line-height: 20px;

  &:hover {
    opacity: 0.6;
  }
}
.main-btn {
  span {
    font-size: 14px;
    font-weight: 600;
    color: white;
  }
}

.sign-in {
  font-size: 16px;
  color: #9da3a5;
}

//чет по ширине
@media (min-width: 920px) {
  a.burger {
    display: none;
  }

  .nav {
    display: flex;
    justify-content: space-between;
    flex-grow: 1;
    .links-container {
      display: flex;
      li.dropdown-item {
        padding-right: 27px;
        font-size: 14px;
        font-weight: 600;
        align-items: center;
      }
    }
  }

  .auth-group {
    display: flex;
    .auth-group-item:not(:nth-child(2)) {
      display: block;
      padding-left: 5px;
    }
  }
  .sign-in {
    margin: 0 13px;
  }
}

.nav-dropdown {
  display: none;
  background-color: #fff;
  position: absolute;
  z-index: 1000;
  left: 0;
  top: 80px;
  width: 100vw;
  box-shadow: 0px 20px 35px rgba(0, 0, 0, 0.1);
}

@media (min-width: 920px) {
  .links-container li:hover .nav-dropdown {
    display: flex;
    padding-left: 72px;
  }
  .mega-nav-box {
    width: 180px;
    padding: 30px 8px;
    margin-right: 40px;

    .title {
      font-size: 16px;
      font-weight: 600;
      color: #333;
      padding-top: 15px;
      padding-bottom: 7px;
    }

    img {
      width: 140px;
      height: 105px;
      border-radius: 8px;
      display: block;
    }

    .mega-description {
      font-size: 13px;
      line-height: 18px;
      font-weight: 400;
      display: inline-block;
    }
  }

  .nav-extras {
    padding-top: 20px;
    font-weight: 500;

    ul {
      flex-direction: column;
      width: 70%;

      li {
        padding: 7px 0;
      }
    }
  }
}
